WebLink.url:
var win = window.open("https://na77.salesforce.com/apex/Email_Collection?scontrolCaching=1&id={!Lead.Id}", "MyWinName", "height=260,width=500"); 
// set the focus new window
win.focus(); 
// assign the methods focusPopup to the window events that may cause it to loose focus
window.onmousedown = focusPopup; 
window.onkeyup = focusPopup; 
window.onmousemove = focusPopup; 

function focusPopup() { 
  if (win && !win.closed) { 
    win.focus(); 
  } 
}

var timer = setInterval(function() {
  if(win.closed) { 
    clearInterval(timer); 
    window.location.href = window.location.href; 
  } 
}, 1000);
